import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate, useParams, useLocation } from 'react-router-dom';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import GlobalStyles from './styles/GlobalStyles';

// Email Confirmation Component
const EmailConfirmation = () => {
  const { pendingConfirmation, confirmEmail, resendConfirmation, cancelConfirmation, isLoading } = useAuth();
  const navigate = useNavigate();
  const [confirmationCode, setConfirmationCode] = React.useState('');
  const [message, setMessage] = React.useState('');

  React.useEffect(() => {
    if (!pendingConfirmation) {
      navigate('/login');
    }
  }, [pendingConfirmation, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');

    try {
      const result = await confirmEmail(confirmationCode);
      if (result.success) {
        navigate('/dashboard');
      } else {
        setMessage('Chyba: ' + result.error);
      }
    } catch (error) {
      setMessage('Chyba: ' + error.message);
    }
  };

  const handleResend = async () => {
    setMessage('');
    try {
      const result = await resendConfirmation();
      if (result.success) {
        setMessage('Potvrzovac칤 email byl znovu odesl치n.');
      } else {
        setMessage('Chyba: ' + result.error);
      }
    } catch (error) {
      setMessage('Chyba: ' + error.message);
    }
  };

  const handleCancel = () => {
    cancelConfirmation();
    navigate('/login');
  };

  if (!pendingConfirmation) {
    return null;
  }

  return (
    <div style={{ padding: '20px', maxWidth: '400px', margin: '100px auto' }}>
      <h2>Potvr캞te sv콢j email</h2>
      <p>
        Na v치코 email <strong>{pendingConfirmation.email}</strong> jsme v치m poslali potvrzovac칤 k칩d.
        Zadejte ho n칤쬰 pro dokon캜en칤 p콏ihl치코en칤.
      </p>

      <div style={{ 
        backgroundColor: '#fff3cd', 
        border: '1px solid #ffeaa7',
        borderRadius: '4px',
        padding: '12px',
        marginBottom: '15px',
        fontSize: '14px',
        color: '#856404'
      }}>
        <strong>游눠 Tip:</strong> Pokud email nevid칤te, zkontrolujte pros칤m slo쬶u <strong>spam/nevy쮂멳an치 po코ta</strong>. 
        Automatick칠 emaily se n캩kdy dost치vaj칤 do t칠to slo쬶y.
      </div>

      {message && (
        <div style={{ 
          padding: '10px', 
          marginBottom: '10px', 
          backgroundColor: message.startsWith('Chyba') ? '#f8d7da' : '#d4edda',
          color: message.startsWith('Chyba') ? '#721c24' : '#155724',
          border: '1px solid ' + (message.startsWith('Chyba') ? '#f5c6cb' : '#c3e6cb'),
          borderRadius: '4px'
        }}>
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '15px' }}>
          <label>Potvrzovac칤 k칩d:</label>
          <input 
            type="text" 
            value={confirmationCode}
            onChange={(e) => setConfirmationCode(e.target.value.toUpperCase())}
            required 
            style={{ 
              width: '100%', 
              padding: '8px',
              textAlign: 'center',
              fontSize: '18px',
              letterSpacing: '2px'
            }}
            placeholder="nap콏. A1B2C3"
            maxLength={6}
          />
        </div>
        <button 
          type="submit" 
          disabled={isLoading || confirmationCode.length !== 6}
          style={{ 
            padding: '10px 20px', 
            backgroundColor: 'var(--color-green)', 
            color: 'white', 
            border: 'none',
            width: '100%',
            marginBottom: '10px',
            opacity: (isLoading || confirmationCode.length !== 6) ? 0.6 : 1
          }}
        >
          {isLoading ? 'Ov캩콏uji...' : 'Potvrdit'}
        </button>
      </form>

      <div style={{ textAlign: 'center', marginTop: '15px' }}>
        <button 
          onClick={handleResend}
          style={{ 
            background: 'none', 
            border: 'none', 
            color: '#007bff', 
            cursor: 'pointer',
            textDecoration: 'underline',
            marginRight: '15px'
          }}
        >
          Odeslat znovu
        </button>
        <button 
          onClick={handleCancel}
          style={{ 
            background: 'none', 
            border: 'none', 
            color: '#6c757d', 
            cursor: 'pointer',
            textDecoration: 'underline'
          }}
        >
          Zp캩t na p콏ihl치코en칤
        </button>
      </div>

      <div style={{ marginTop: '20px', fontSize: '14px', color: '#6c757d' }}>
        <p>Tip: Potvrzovac칤 k칩d je platn칳 15 minut a obsahuje 6 znak콢 (p칤smena a 캜칤sla).</p>
      </div>
    </div>
  );
};

// Simple Login Component
const Login = () => {
  const { login, isAuthenticated, pendingConfirmation } = useAuth();
  const navigate = useNavigate();

  // Redirect if already logged in or pending confirmation
  React.useEffect(() => {
    if (isAuthenticated) {
      navigate('/dashboard');
    } else if (pendingConfirmation) {
      navigate('/confirm-email');
    }
  }, [isAuthenticated, pendingConfirmation, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    try {
      const result = await login({
        username: formData.get('username'),
        password: formData.get('password')
      });
      if (result.success) {
        if (result.requiresConfirmation) {
          navigate('/confirm-email');
        } else {
          navigate('/dashboard');
        }
      } else {
        alert('P콏ihl치코en칤 se nezda콏ilo: ' + result.error);
      }
    } catch (error) {
      alert('P콏ihl치코en칤 se nezda콏ilo: ' + error.message);
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '400px', margin: '100px auto' }}>
      <h2>Zp캩tn치 vazba - P콏ihl치코en칤</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '10px', marginTop: '20px', }}>
          <input 
            type="text" 
            name="username" 
            placeholder="U쬴vatelsk칠 jm칠no"
            required 
            style={{ 
              width: '100%', 
              height: '45px',
              padding: '8px', 
              borderRadius: '8px',
              border: '1px solid #ced4da',
              fontSize: '16px',
              fontFamily: 'Roboto, sans-serif'
            }} 
          />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <input 
            type="password" 
            name="password" 
            placeholder="Heslo"
            required 
            style={{ 
              width: '100%', 
              height: '45px',
              padding: '8px', 
              borderRadius: '8px',
              border: '1px solid #ced4da',
              fontSize: '16px',
              fontFamily: 'Roboto, sans-serif'
            }} 
          />
        </div>
        <button className='button-confirm' type="submit" style={{height: '45px', borderRadius: '0px'}}>
          P콏ihl치sit se
        </button>
      </form>
