            title: '칔vodn칤 sekce',
            description: 'Syst칠mov치 칰vodn칤 캜치st (pouze admin)',
            isAdminOnly: true,
            isEditable: true,
            steps: []
          },
          {
            id: 4,
            title: 'Z치v캩re캜n치 sekce',
            description: 'Syst칠mov치 z치v캩re캜n치 캜치st (pouze admin)',
            isAdminOnly: true,
            isEditable: true,
            steps: []
          }
        ]
      };
    } else {
      return {
        title: '',
        description: '',
        cards: [
          {
            id: 0,
            title: 'Floating Widget',
            description: 'Floating feedback widget',
            isAdminOnly: false,
            isEditable: false,
            isSystem: true,
            steps: [
              {
                id: 'floating-widget',
                type: 'floating-widget',
                question: 'Floating feedback widget',
                isSystem: true,
                uneditable: true
              }
            ]
          },
          {
            id: 1,
            title: 'Feedback Modal',
            description: 'Feedback options',
            isAdminOnly: false,
            isEditable: false,
            isSystem: true,
            steps: [
              {
                id: 'feedback-modal',
                type: 'feedback-modal',
                question: 'Feedback options',
                isSystem: true,
                uneditable: true
              }
            ]
          },
          {
            id: 2,
            title: '칔vodn칤 sekce',
            description: 'Syst칠mov치 칰vodn칤 캜치st (pouze admin)',
            isAdminOnly: true,
            isEditable: false,
            steps: []
          },
          {
            id: 3,
            title: 'Hlavn칤 obsah',
            description: 'Editovateln치 캜치st pro u쬴vatele',
            isAdminOnly: false,
            isEditable: true,
            steps: []
          },
          {
            id: 4,
            title: 'Z치v캩re캜n치 sekce',
            description: 'Syst칠mov치 z치v캩re캜n치 캜치st (pouze admin)',
            isAdminOnly: true,
            isEditable: false,
            steps: []
          }
        ]
      };
    }
  };

  const [survey, setSurvey] = React.useState(getInitialSurvey());

  // Load existing survey for editing or global sections
  React.useEffect(() => {
    if (globalMode) {
      loadGlobalSections();
    } else if (editingSurveyId) {
      loadSurveyForEditing(editingSurveyId);
    } else {
      loadGlobalSectionsForUserSurvey();
    }
  }, [editingSurveyId, globalMode]);

  const loadGlobalSections = async () => {
    try {
      setLoadingSurvey(true);
      const response = await fetch('/api/global-surveys/sections', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setSurvey(prev => ({
          ...prev,
          cards: prev.cards.map(card => {
            if (card.id === 2) { // 칔vodn칤 sekce
              return { ...card, steps: data.introSection || [] };
            } else if (card.id === 4) { // Z치v캩re캜n치 sekce
              return { ...card, steps: data.outroSection || [] };
            }
            return card;
          })
        }));
      }
    } catch (error) {
      console.error('Error loading global sections:', error);
    } finally {
      setLoadingSurvey(false);
    }
  };

  const loadGlobalSectionsForUserSurvey = async () => {
    try {
      const response = await fetch('/api/global-surveys/sections', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();

        // Update the intro and outro sections with global content
        setSurvey(prev => ({
          ...prev,
          cards: prev.cards.map(card => {
            if (card.id === 2) { // 칔vodn칤 sekce
              return { ...card, steps: data.introSection || [] };
            } else if (card.id === 4) { // Z치v캩re캜n치 sekce
              return { ...card, steps: data.outroSection || [] };
            }
            return card;
          })
        }));
      }
    } catch (error) {
      console.error('Error loading global sections:', error);
    }
  };

  const loadSurveyForEditing = async (surveyId) => {
    try {
      setLoadingSurvey(true);
      const response = await fetch(`/api/surveys/${surveyId}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const surveyData = await response.json();
        setSurvey({
          title: surveyData.title,
          description: surveyData.description,
          cards: surveyData.config.cards || survey.cards // Use existing cards as fallback
        });
        setIsEditMode(true);
        setOriginalSurveyId(surveyId);
      } else {
        throw new Error('Chyba p콏i na캜칤t치n칤 dotazn칤ku');
      }
    } catch (error) {
      console.error('Error loading survey:', error);
      alert('Chyba p콏i na캜칤t치n칤 dotazn칤ku: ' + error.message);
      navigate('/surveys');
    } finally {
      setLoadingSurvey(false);
    }
  };

  const [activeCardIndex, setActiveCardIndex] = React.useState(globalMode ? 0 : 2); // Default to first global card in global mode

  const [editingStep, setEditingStep] = React.useState(null);
  const [showStepEditor, setShowStepEditor] = React.useState(false);
  const [draggedStep, setDraggedStep] = React.useState(null);
  const [dragOverIndex, setDragOverIndex] = React.useState(null);

  const saveSurvey = async () => {
    try {
      if (globalMode) {
        // Save global sections
        const introCard = survey.cards.find(card => card.id === 2);
        const outroCard = survey.cards.find(card => card.id === 4);

        const response = await fetch('/api/global-surveys/sections', {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          },
          body: JSON.stringify({
            introSection: introCard?.steps || [],
            outroSection: outroCard?.steps || []
          })
        });

        if (response.ok) {
          alert('Glob치ln칤 sekce byly 칰sp캩코n캩 ulo쬰ny!');
        } else {
          throw new Error('Chyba p콏i ukl치d치n칤 glob치ln칤ch sekc칤');
        }
      } else {
        // Regular survey save
        const url = isEditMode ? `/api/surveys/${originalSurveyId}` : '/api/surveys';
        const method = isEditMode ? 'PUT' : 'POST';

        const response = await fetch(url, {
          method: method,
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          },
          body: JSON.stringify({
            title: survey.title,
            description: survey.description,
            config: {
              cards: survey.cards,
              steps: survey.cards.flatMap(card => card.steps || [])
            }
          })
        });

        if (response.ok) {
          if (isEditMode) {
            alert('Pr콢zkum byl 칰sp캩코n캩 aktualizov치n!');
            navigate('/surveys');
          } else {
            alert('Pr콢zkum byl 칰sp캩코n캩 ulo쬰n!');
            // Reset form for new survey
            setSurvey(getInitialSurvey());
            setCurrentStep(1);
          }
        } else {
          alert(isEditMode ? 'Chyba p콏i aktualizaci pr콢zkumu' : 'Chyba p콏i ukl치d치n칤 pr콢zkumu');
        }
      }
    } catch (error) {
      console.error('Error saving survey:', error);
      alert(globalMode ? 'Chyba p콏i ukl치d치n칤 glob치ln칤ch sekc칤' : (isEditMode ? 'Chyba p콏i aktualizaci pr콢zkumu' : 'Chyba p콏i ukl치d치n칤 pr콢zkumu'));
    }
  };

  const addStep = (stepData) => {
    const newStep = {
      id: Date.now(),
      ...stepData
    };
    setSurvey(prev => ({
      ...prev,
      cards: prev.cards.map((card, index) => 
        index === activeCardIndex 
          ? { ...card, steps: [...card.steps, newStep] }
          : card
      )
    }));
    setShowStepEditor(false);
    setEditingStep(null);
  };

  const updateStep = (stepData) => {
    setSurvey(prev => ({
      ...prev,
      cards: prev.cards.map((card, index) => 
        index === activeCardIndex 
          ? { 
              ...card, 
              steps: card.steps.map(step => 
                step.id === editingStep.id ? { ...editingStep, ...stepData } : step
              )
            }
          : card
      )
    }));
    setShowStepEditor(false);
    setEditingStep(null);
  };

  const deleteStep = (stepId, cardIndex) => {
    if (window.confirm('Opravdu chcete smazat tento blok?')) {
      setSurvey(prev => ({
        ...prev,
        cards: prev.cards.map((card, index) => 
          index === cardIndex 
            ? { ...card, steps: card.steps.filter(step => step.id !== stepId) }
            : card
        )
      }));
    }
  };

  const moveStep = (stepId, direction) => {
    const activeCard = survey.cards[activeCardIndex];
    if (!activeCard) return;

    const steps = [...activeCard.steps];
    const currentIndex = steps.findIndex(step => step.id === stepId);
    const newIndex = direction === 'up' ? currentIndex - 1 : currentIndex + 1;

    if (newIndex >= 0 && newIndex < steps.length) {
      [steps[currentIndex], steps[newIndex]] = [steps[newIndex], steps[currentIndex]];
      setSurvey(prev => ({
        ...prev,
        cards: prev.cards.map((card, index) => 
          index === activeCardIndex ? { ...card, steps } : card
        )
      }));
    }
  };

  // Drag and Drop handlers
  const handleDragStart = (e, index) => {
    setDraggedStep(index);
    e.dataTransfer.effectAllowed = 'move';
    e.dataTransfer.setData('text/html', e.target.parentNode);
    e.dataTransfer.setDragImage(e.target, 0, 0);
  };

  const handleDragOver = (e, index) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
    setDragOverIndex(index);
  };

  const handleDragLeave = (e) => {
    // Only clear drag over if we're actually leaving the component
    if (!e.currentTarget.contains(e.relatedTarget)) {
      setDragOverIndex(null);
    }
  };

  const handleDrop = (e, dropIndex) => {
    e.preventDefault();

    if (draggedStep === null || draggedStep === dropIndex) {
      setDraggedStep(null);
      setDragOverIndex(null);
      return;
    }

    const activeCard = survey.cards[activeCardIndex];
    if (!activeCard) {
      setDraggedStep(null);
      setDragOverIndex(null);
      return;
    }

    const newSteps = [...activeCard.steps];
    const draggedItem = newSteps[draggedStep];

    // Remove dragged item
    newSteps.splice(draggedStep, 1);

    // Insert at new position
    const actualDropIndex = draggedStep < dropIndex ? dropIndex - 1 : dropIndex;
    newSteps.splice(actualDropIndex, 0, draggedItem);

    setSurvey(prev => ({
      ...prev,
      cards: prev.cards.map((card, index) => 
        index === activeCardIndex ? { ...card, steps: newSteps } : card
      )
    }));
    setDraggedStep(null);
    setDragOverIndex(null);
  };

  const handleDragEnd = () => {
    setDraggedStep(null);
    setDragOverIndex(null);
  };

  if (loadingSurvey) {
    return (
      <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto', textAlign: 'center' }}>
        <h2>Tvorba dotazn칤ku</h2>
        <div style={{ marginTop: '50px' }}>
          <div style={{ fontSize: '18px', marginBottom: '20px' }}>Na캜칤t치n칤 dotazn칤ku...</div>
          <div style={{ fontSize: '14px', color: '#6c757d' }}>Pros칤m po캜kejte, ne se na캜tou data dotazn칤ku</div>
        </div>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', maxWidth: '1200px', margin: '0 auto' }}>
      <h2>{globalMode ? 'Spr치va glob치ln칤ch sekc칤' : (isEditMode ? 'Editace dotazn칤ku' : 'Tvorba dotazn칤ku')}</h2>
      <p style={{ color: '#6c757d', marginBottom: '30px' }}>
        {globalMode 
          ? 'Upravte glob치ln칤 sekce "칔vodn칤 sekce" a "Z치v캩re캜n치 sekce" sd칤len칠 nap콏칤캜 v코emi pr콢zkumy' 
          : (isEditMode ? 'Upravujte existuj칤c칤 pr콢zkum a zm캩ny budou ulo쬰ny jako nov치 verze' : 'Vytvo콏te vlastn칤 pr콢zkum pomoc칤 r콢zn칳ch stavebn칤ch blok콢')
        }
      </p>

      {isEditMode && (
        <div style={{ 
          backgroundColor: '#e3f2fd', 
          border: '1px solid #2196f3', 
          borderRadius: '8px', 
          padding: '16px', 
          marginBottom: '20px' 
        }}>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
            <span style={{ fontSize: '20px', marginRight: '8px' }}>九勇</span>
            <strong style={{ color: '#1976d2' }}>EDITA캛N칈 RE콯IM</strong>
          </div>
          <p style={{ margin: '0', color: '#1976d2', fontSize: '14px' }}>
            Upravujete dotazn칤k: <strong>"{survey.title}"</strong>. 
            Po ulo쬰n칤 bude vytvo콏ena nov치 aktualizovan치 verze pr콢zkumu.
          </p>
        </div>
      )}

      {/* Survey Basic Info */}
      {currentStep === 1 && (
        <div style={{ 
          backgroundColor: '#f8f9fa', 
          padding: '30px', 
          borderRadius: '8px', 
          marginBottom: '20px',
          border: '1px solid #dee2e6'
        }}>
          <h3>Krok 1: Z치kladn칤 informace</h3>

          <div style={{ marginBottom: '20px' }}>
            <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>
              N치zev pr콢zkumu *
            </label>
            <input
              type="text"
              value={survey.title}
              onChange={(e) => setSurvey(prev => ({ ...prev, title: e.target.value }))}
              style={{ 
                width: '100%', 
                padding: '12px', 
                border: '1px solid #ced4da', 
                borderRadius: '4px',
                fontSize: '16px'
              }}
              placeholder="Zadejte n치zev va코eho pr콢zkumu"
            />
          </div>

          <div style={{ marginBottom: '30px' }}>
            <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>
              Popis pr콢zkumu
            </label>
            <textarea
              value={survey.description}
              onChange={(e) => setSurvey(prev => ({ ...prev, description: e.target.value }))}
              style={{ 
                width: '100%', 
                padding: '12px', 
                border: '1px solid #ced4da', 
                borderRadius: '4px',
                fontSize: '16px',
                minHeight: '100px',
                resize: 'vertical'
              }}
              placeholder="Popi코te 칰캜el a obsah va코eho pr콢zkumu"
            />
