const SurveyStepEditor = ({ step, onSave, onCancel }) => {
  const [formData, setFormData] = React.useState({
    type: step?.type || 'smiley',
    question: step?.question || '',
    options: step?.options || [],
    required: step?.required || true,
    conditionalTriggers: step?.conditionalTriggers || [],
    hasTextbox: step?.hasTextbox || false,
    textboxPlaceholder: step?.textboxPlaceholder || ''
  });

  const [newOption, setNewOption] = React.useState('');

  const addOption = () => {
    if (newOption.trim()) {
      setFormData(prev => ({
        ...prev,
        options: [...prev.options, newOption.trim()]
      }));
      setNewOption('');
    }
  };

  const removeOption = (index) => {
    setFormData(prev => ({
      ...prev,
      options: prev.options.filter((_, i) => i !== index)
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.question.trim()) {
      alert('Zadejte pros칤m ot치zku');
      return;
    }
    if ((formData.type === 'single-choice' || formData.type === 'multi-select' || formData.type === 'rating-scale') && formData.type !== 'section-header' && formData.options.length === 0) {
      alert('P콏idejte pros칤m alespo켿 jednu mo쬹ost');
      return;
    }
    onSave(formData);
  };

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0,0,0,0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000
    }}>
      <div style={{
        backgroundColor: 'white',
        borderRadius: '8px',
        padding: '30px',
        maxWidth: '600px',
        width: '90%',
        maxHeight: '80vh',
        overflow: 'auto'
      }}>
        <h3>{step ? 'Upravit blok' : 'P콏idat nov칳 blok'}</h3>

        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: '20px' }}>
            <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>
              Typ ot치zky *
            </label>
            <select
              value={formData.type}
              onChange={(e) => setFormData(prev => ({ ...prev, type: e.target.value }))}
              style={{
                width: '100%',
                padding: '10px',
                border: '1px solid #ced4da',
                borderRadius: '4px'
              }}
                         >
               <option value="smiley">5-bodov칠 hodnocen칤 smajl칤ky</option>
               <option value="single-choice">Jednoduch칠 v칳b캩r</option>
               <option value="dropdown-multiselect">V칤cen치sobn칳 v칳b캩r (dropdown)</option>
               <option value="text">Textov칠 pole</option>
               <option value="section-header">Nadpis sekce</option>
             </select>
          </div>

                     <div style={{ marginBottom: '20px' }}>
             <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>
               {formData.type === 'section-header' ? 'N치zev sekce *' : 'Ot치zka *'}
             </label>
             <input
               type="text"
               value={formData.question}
               onChange={(e) => setFormData(prev => ({ ...prev, question: e.target.value }))}
               style={{
                 width: '100%',
                 padding: '10px',
                 border: '1px solid #ced4da',
                 borderRadius: '4px'
               }}
               placeholder={formData.type === 'section-header' ? 'Zadejte n치zev sekce' : 'Zadejte text ot치zky'}
             />
           </div>

                     {(formData.type === 'single-choice' || formData.type === 'dropdown-multiselect') && formData.type !== 'section-header' && (
            <div style={{ marginBottom: '20px' }}>
              <label style={{ display: 'block', marginBottom: '8px', fontWeight: 'bold' }}>
                Mo쬹osti odpov캩d칤
              </label>

              {formData.options.map((option, index) => (
                <div key={index} style={{ display: 'flex', marginBottom: '8px', alignItems: 'center' }}>
                  <input
                    type="text"
                    value={option}
                    onChange={(e) => {
                      const newOptions = [...formData.options];
