                              // Add trigger
                              setFormData(prev => ({
                                ...prev,
                                conditionalTriggers: [
                                  ...prev.conditionalTriggers,
                                  {
                                    optionIndex: optionIndex,
                                    optionText: option,
                                    textboxPlaceholder: 'Pros칤m, okomentujte',
                                    textboxLabel: 'Nepovinn칠 pole'
                                  }
                                ]
                              }));
                            } else {
                              // Remove trigger
                              setFormData(prev => ({
                                ...prev,
                                conditionalTriggers: prev.conditionalTriggers.filter(t => t.optionIndex !== optionIndex)
                              }));
                            }
                          }}
                          style={{ marginRight: '6px' }}
                        />
                        <span style={{ fontSize: '13px', color: '#6c757d' }}>
                          Textov칠 pole
                        </span>
                      </label>
                    </div>

                    {trigger && (
                      <div style={{ marginTop: '8px' }}>
                        <input
                          type="text"
                          value={trigger.textboxPlaceholder}
                          onChange={(e) => {
                            setFormData(prev => ({
                              ...prev,
                              conditionalTriggers: prev.conditionalTriggers.map(t => 
                                t.optionIndex === optionIndex 
                                  ? { ...t, textboxPlaceholder: e.target.value }
                                  : t
                              )
                            }));
                          }}
                          style={{
                            width: '100%',
                            padding: '6px 8px',
                            border: '1px solid #ced4da',
                            borderRadius: '4px',
