      const stateFolder = currentState === 'active' ? 'activeState' : 
                         currentState === 'hover' ? 'hoverState' : 'defaultState';
      return `/icons/${stateFolder}/${iconName}.svg`;
    };

    return (
      <img 
        src={getIconPath()}
        alt={iconName}
        width={size}
        height={size}
        style={{
          cursor: 'pointer',
          transition: 'all 0.2s ease',
