// Survey Canvas Component
const SurveyCanvas = ({ steps, onStepsChange, onEditStep, onDeleteStep, cardInfo, canEdit, isAdmin }) => {
  const [dragOverIndex, setDragOverIndex] = React.useState(null);
  const [draggedStepIndex, setDraggedStepIndex] = React.useState(null);

  const handleDragOver = (e, index) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'copy';
    setDragOverIndex(index);
  };

  const handleDrop = (e, dropIndex) => {
    e.preventDefault();

    // Check if editing is allowed
    if (!canEdit) {
      alert(cardInfo?.isAdminOnly ? 'Tato sekce m콢쬰 b칳t upravov치na pouze administr치torem' : 'Tato sekce nen칤 editovateln치');
      setDragOverIndex(null);
      setDraggedStepIndex(null);
      return;
    }

    const data = e.dataTransfer.getData('application/json');

    try {
      const dragData = JSON.parse(data);

      if (dragData.type === 'building-block') {
        // Adding new block from palette
        const newStep = {
          id: Date.now(),
          type: dragData.blockType,
          question: getDefaultQuestion(dragData.blockType),
          options: getDefaultOptions(dragData.blockType),
          required: true,
          conditional: false
        };

        const newSteps = [...steps];
        newSteps.splice(dropIndex, 0, newStep);
        onStepsChange(newSteps);
      } else if (dragData.type === 'existing-step') {
        // Reordering existing steps
        const dragIndex = dragData.index;
        if (dragIndex !== dropIndex) {
          const newSteps = [...steps];
          const draggedItem = newSteps[dragIndex];
          newSteps.splice(dragIndex, 1);
          const actualDropIndex = dragIndex < dropIndex ? dropIndex - 1 : dropIndex;
          newSteps.splice(actualDropIndex, 0, draggedItem);
          onStepsChange(newSteps);
        }
      }
    } catch (error) {
      console.error('Error parsing drag data:', error);
    }

    setDragOverIndex(null);
    setDraggedStepIndex(null);
  };

  const handleStepDragStart = (e, index) => {
    e.dataTransfer.setData('application/json', JSON.stringify({
      type: 'existing-step',
      index: index
    }));
    e.dataTransfer.effectAllowed = 'move';
    setDraggedStepIndex(index);
  };

  const getDefaultQuestion = (blockType) => {
    switch (blockType) {
      case 'smiley': return 'Jak hodnot칤te na코i slu쬭u?';
      case 'single-choice': return 'Vyberte jednu mo쬹ost:';
      case 'dropdown-multiselect': return 'Vyberte v코echny vhodn칠 mo쬹osti:';
      case 'text': return 'V치코 koment치콏 nebo p콏ipom칤nka:';
      case 'section-header': return 'N치zev sekce';
      default: return 'Nov치 ot치zka';
    }
  };

  const getDefaultOptions = (blockType) => {
    switch (blockType) {
      case 'single-choice': return ['Mo쬹ost 1', 'Mo쬹ost 2', 'Mo쬹ost 3'];
      case 'dropdown-multiselect': return ['Mo쬹ost A', 'Mo쬹ost B', 'Mo쬹ost C'];
      default: return [];
    }
  };

  // Create drop zones between steps and at the end
  const dropZones = [];
  for (let i = 0; i <= steps.length; i++) {
    dropZones.push(
      <div
        key={`drop-${i}`}
        onDragOver={(e) => handleDragOver(e, i)}
        onDrop={(e) => handleDrop(e, i)}
        onDragLeave={() => setDragOverIndex(null)}
        style={{
          height: dragOverIndex === i ? '40px' : '20px',
          backgroundColor: dragOverIndex === i ? '#e3f2fd' : 'transparent',
          border: dragOverIndex === i ? '2px dashed #2196f3' : '2px dashed transparent',
          borderRadius: '8px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          transition: 'all 0.2s ease',
          margin: '5px 0'
        }}
      >
        {dragOverIndex === i && (
          <span style={{ color: '#2196f3', fontSize: '12px', fontWeight: 'bold' }}>
            拘勇 Upustit zde
          </span>
        )}
      </div>
    );

    // Add step card after each drop zone (except the last one)
    if (i < steps.length) {
      dropZones.push(
        <SurveyStepCard
          key={steps[i].id}
          step={steps[i]}
          index={i}
          onEdit={canEdit ? () => onEditStep(steps[i]) : undefined}
          onDelete={canEdit ? () => onDeleteStep(steps[i].id) : undefined}
          onDragStart={canEdit ? handleStepDragStart : undefined}
          isDragging={draggedStepIndex === i}
          isInCanvas={true}
          canEdit={canEdit}
        />
      );
    }
  }

  const getEmptyStateContent = () => {
    if (!canEdit) {
      return {
        icon: '游',
        title: cardInfo?.isAdminOnly ? 'Sekce pouze pro administr치tory' : 'Sekce jen pro 캜ten칤',
        message: cardInfo?.isAdminOnly 
          ? 'Tato sekce m콢쬰 b칳t upravov치na pouze administr치torem'
          : 'Tato sekce nen칤 editovateln치, ale m콢쬰te si prohl칠dnout jej칤 obsah'
      };
    }

    if (dragOverIndex === 0) {
      return {
        icon: '拘勇',
        title: 'Upustit zde!',
        message: 'Vytvo콏te sv콢j prvn칤 blok v tomto kroku'
      };
    }

    return {
      icon: '游늶',
      title: 'Pr치zdn칳 krok',
      message: 'P콏et치hn캩te stavebn칤 bloky sem pro vytvo콏en칤 obsahu tohoto kroku'
    };
  };

  return (
    <div style={{ padding: '20px', minHeight: '400px' }}>
      {/* Card Header */}
      <div style={{ 
        marginBottom: '20px', 
        padding: '15px',
        backgroundColor: cardInfo?.isAdminOnly ? '#fff3cd' : '#e7f3ff',
        border: `1px solid ${cardInfo?.isAdminOnly ? '#ffeaa7' : '#b3d7ff'}`,
        borderRadius: '6px'
      }}>
                 <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div>
            <h4 style={{ margin: '0 0 4px 0', color: '#495057' }}>
              {cardInfo?.title} - Krok pr콢zkumu
              {cardInfo?.isAdminOnly && (
                <span style={{ 
                  marginLeft: '8px', 
                  fontSize: '11px',
                  backgroundColor: 'var(--color-red)',
                  color: 'white',
                  padding: '2px 6px',
                  borderRadius: '3px'
                }}>
                  ADMIN ONLY
                </span>
              )}
            </h4>
            <small style={{ color: '#6c757d' }}>{cardInfo?.description}</small>
          </div>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '10px'
          }}>
                         <span style={{ 
               backgroundColor: '#6c757d',
               color: 'white',
               padding: '4px 8px',
               borderRadius: '12px',
               fontSize: '12px',
               fontWeight: 'bold'
             }}>
               {steps.length} {steps.length === 1 ? 'blok' : steps.length < 5 ? 'bloky' : 'blok콢'}
             </span>
            {!canEdit && <span style={{ fontSize: '20px' }}>游</span>}
          </div>
        </div>
      </div>

      {steps.length === 0 ? (
        <div
          onDragOver={canEdit ? (e) => handleDragOver(e, 0) : undefined}
          onDrop={canEdit ? (e) => handleDrop(e, 0) : undefined}
          onDragLeave={canEdit ? () => setDragOverIndex(null) : undefined}
          style={{
            textAlign: 'center',
            padding: '80px 20px',
            border: `3px dashed ${canEdit && dragOverIndex === 0 ? '#2196f3' : '#dee2e6'}`,
            borderRadius: '12px',
            backgroundColor: canEdit && dragOverIndex === 0 ? '#e3f2fd' : '#f8f9fa',
            color: '#6c757d',
            transition: 'all 0.3s ease',
            opacity: canEdit ? 1 : 0.7
          }}
        >
          {(() => {
            const content = getEmptyStateContent();
            return (
              <>
                <div style={{ fontSize: '48px', marginBottom: '20px' }}>
                  {content.icon}
                </div>
                <h4 style={{ margin: '0 0 10px 0' }}>
                  {content.title}
                </h4>
                <p style={{ margin: 0 }}>
                  {content.message}
                </p>
              </>
            );
          })()}
        </div>
      ) : (
        <div style={{ opacity: canEdit ? 1 : 0.8 }}>
          {dropZones}
        </div>
      )}
    </div>
  );
};

// Survey Card Manager Component
const SurveyCardManager = ({ survey, activeCardIndex, onActiveCardChange, onSurveyChange, onEditStep, onDeleteStep, user, globalMode = false }) => {
  const navigate = useNavigate();
  const isAdmin = user?.role === 'admin';

  const handleStepsChange = (cardIndex, newSteps) => {
    onSurveyChange(prev => ({
      ...prev,
      cards: prev.cards.map((card, index) => 
        index === cardIndex ? { ...card, steps: newSteps } : card
      )
    }));
  };

  const canEditCard = (card) => {
    if (card.isGlobal && !globalMode) {
      return false; // Global cards are not editable in regular survey creator
    }
    if (card.isAdminOnly) {
      return isAdmin;
    }
    return card.isEditable;
  };

  const handleCardClick = (cardIndex) => {
    const card = survey.cards[cardIndex];

    // Show warning for global sections in regular mode
    if (!globalMode && (card.id === 2 || card.id === 4)) {
      if (isAdmin) {
        const shouldRedirect = window.confirm(
          `Pokou코칤te se editovat glob치ln칤 sekci "${card.title}".\n\n` +
          `Pro lep코칤 spr치vu glob치ln칤ch sekc칤 doporu캜ujeme pou쮂셦 dedikovanou str치nku "Global Surveys".\n\n` +
          `Chcete p콏ej칤t na Global Surveys nyn칤?`
        );

        if (shouldRedirect) {
          navigate('/global-surveys');
          return;
        }
      } else {
        alert(
          `Sekce "${card.title}" je glob치ln칤 a spravuje ji administr치tor.\n\n` +
          `Tato sekce je automaticky zahrnuta do v코ech dotazn칤k콢 a nelze ji zde upravovat.`
        );
        return;
      }
    }

    onActiveCardChange(cardIndex);
  };

  const canDeleteCard = (card, cardIndex) => {
    // Cannot delete system cards or admin cards
    // Cannot delete if it's the only user card
    const userCards = survey.cards.filter(c => !c.isAdminOnly && !c.isSystem);
    return !card.isAdminOnly && !card.isSystem && userCards.length > 1;
  };

  const addNewCard = () => {
    const userCards = survey.cards.filter(c => !c.isAdminOnly && !c.isSystem);
    const newCardNumber = userCards.length + 1;
    const lastAdminCardIndex = survey.cards.length - 1; // Last card is always admin "Z치v캩re캜n치 sekce"

    const newCard = {
      id: Date.now(),
      title: `Krok ${newCardNumber}`,
      description: `U쬴vatelsk치 sekce ${newCardNumber}`,
      isAdminOnly: false,
      isEditable: true,
      steps: []
    };

    onSurveyChange(prev => {
      const newCards = [...prev.cards];
      // Insert before the last admin card
      newCards.splice(lastAdminCardIndex, 0, newCard);
      return {
        ...prev,
        cards: newCards
      };
    });
  };

  const deleteCard = (cardIndex) => {
    const card = survey.cards[cardIndex];
    if (!canDeleteCard(card, cardIndex)) return;

    if (window.confirm(`Opravdu chcete smazat "${card.title}"? V코echny bloky v t칠to sekci budou ztraceny.`)) {
      onSurveyChange(prev => ({
        ...prev,
        cards: prev.cards.filter((_, index) => index !== cardIndex)
      }));

      // Adjust active card index if needed
      if (activeCardIndex === cardIndex) {
        // Move to the previous card or first editable card
        const newActiveIndex = cardIndex > 0 ? cardIndex - 1 : 0;
        onActiveCardChange(newActiveIndex);
      } else if (activeCardIndex > cardIndex) {
        onActiveCardChange(activeCardIndex - 1);
      }
    }
  };

  const renameCard = (cardIndex, newTitle) => {
    onSurveyChange(prev => ({
      ...prev,
      cards: prev.cards.map((card, index) => 
        index === cardIndex ? { ...card, title: newTitle } : card
      )
    }));
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', height: '100%' }}>
      {/* Card Management Panel */}
      <div style={{
        backgroundColor: '#f8f9fa',
        padding: '12px 20px',
        borderBottom: '2px solid #dee2e6',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        gap: '15px'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <span style={{ 
            fontWeight: 'bold', 
            color: '#495057',
            fontSize: '14px'
          }}>
            游늶 Spr치va krok콢:
          </span>

          <button
            onClick={addNewCard}
            style={{
              display: 'flex',
              alignItems: 'center',
              gap: '5px',
              padding: '8px 12px',
              backgroundColor: 'var(--color-green)',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              fontSize: '13px',
              fontWeight: 'bold',
              cursor: 'pointer',
              transition: 'all 0.2s ease'
            }}
            onMouseEnter={(e) => e.target.style.backgroundColor = '#218838'}
            onMouseLeave={(e) => e.target.style.backgroundColor = 'var(--color-green)'}
            title="P콏idat nov칳 krok mezi u쬴vatelsk칠 sekce"
          >
            俱 P콏idat krok
          </button>
        </div>

        <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
          <small style={{ color: '#6c757d', fontSize: '12px' }}>
            游눠 Tip: Kliknut칤m na krok ho aktivujete, syst칠mov칠 kroky 游 m콢쬰 editovat pouze admin
          </small>

          <div style={{ 
            backgroundColor: '#e9ecef',
            padding: '4px 8px',
            borderRadius: '4px',
            fontSize: '12px',
            fontWeight: 'bold',
            color: '#495057'
          }}>
            {survey.cards.filter(card => !card.isSystem).length} {survey.cards.filter(card => !card.isSystem).length === 1 ? 'krok' : survey.cards.filter(card => !card.isSystem).length < 5 ? 'kroky' : 'krok콢'}
          </div>
        </div>
      </div>

      {/* Card Tabs - Hide system cards */}
      <div style={{ 
        display: 'flex', 
        borderBottom: '1px solid #dee2e6',
        backgroundColor: '#f8f9fa',
        borderRadius: '0'
      }}>
        {survey.cards
          .filter(card => !card.isSystem) // Hide system cards from the interface
          .map((card, filteredIndex) => {
            // Find the original index in the unfiltered array
            const originalIndex = survey.cards.findIndex(c => c.id === card.id);
            return (
              <CardTab
                key={card.id}
                card={card}
                index={originalIndex}
                isActive={activeCardIndex === originalIndex}
                canEdit={canEditCard(card)}
                canDelete={canDeleteCard(card, originalIndex)}
                isAdmin={isAdmin}
                onClick={() => handleCardClick(originalIndex)}
                onDelete={() => deleteCard(originalIndex)}
                onRename={(newTitle) => renameCard(originalIndex, newTitle)}
              />
            );
          })}
      </div>

      {/* Active Card Content */}
      <div style={{ flex: 1, backgroundColor: 'white' }}>
        {survey.cards[activeCardIndex] && (
          <SurveyCanvas 
            steps={survey.cards[activeCardIndex].steps}
            onStepsChange={(newSteps) => handleStepsChange(activeCardIndex, newSteps)}
            onEditStep={onEditStep}
            onDeleteStep={(stepId) => onDeleteStep(stepId, activeCardIndex)}
            cardInfo={survey.cards[activeCardIndex]}
            canEdit={canEditCard(survey.cards[activeCardIndex])}
            isAdmin={isAdmin}
          />
        )}
      </div>
    </div>
  );
};

// New CardTab component for individual card management
const CardTab = ({ card, index, isActive, canEdit, canDelete, isAdmin, onClick, onDelete, onRename }) => {
  const [isEditing, setIsEditing] = React.useState(false);
  const [editTitle, setEditTitle] = React.useState(card.title);

  const handleDoubleClick = () => {
    if (canEdit && !card.isAdminOnly) {
      setIsEditing(true);
      setEditTitle(card.title);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSaveEdit();
    } else if (e.key === 'Escape') {
      setIsEditing(false);
      setEditTitle(card.title);
    }
  };

  const handleSaveEdit = () => {
    if (editTitle.trim() && editTitle !== card.title) {
      onRename(editTitle.trim());
    }
    setIsEditing(false);
  };

  return (
    <div
      onClick={!isEditing ? onClick : undefined}
      onDoubleClick={handleDoubleClick}
      style={{
        flex: 1,
        padding: '15px 20px',
        cursor: isEditing ? 'default' : 'pointer',
        backgroundColor: isActive ? '#007bff' : 'transparent',
        color: isActive ? 'white' : (card.isAdminOnly && !isAdmin ? '#999' : '#495057'),
        borderBottom: isActive ? '3px solid #0056b3' : '3px solid transparent',
        transition: 'all 0.2s ease',
        opacity: card.isAdminOnly && !isAdmin ? 0.6 : 1,
        position: 'relative',
        minWidth: '180px'
      }}
      onMouseEnter={(e) => {
        if (!isActive && (canEdit || !card.isAdminOnly) && !isEditing) {
          e.target.style.backgroundColor = '#e9ecef';
        }
      }}
      onMouseLeave={(e) => {
        if (!isActive && !isEditing) {
          e.target.style.backgroundColor = 'transparent';
        }
      }}
    >
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <div style={{ flex: 1 }}>
          {isEditing ? (
            <input
              type="text"
              value={editTitle}
              onChange={(e) => setEditTitle(e.target.value)}
              onBlur={handleSaveEdit}
              onKeyDown={handleKeyPress}
              style={{
                backgroundColor: 'white',
                color: '#000',
                border: '2px solid #007bff',
                borderRadius: '3px',
                padding: '4px 8px',
                fontSize: '14px',
                fontWeight: 'bold',
                width: '100%',
                outline: 'none'
              }}
              autoFocus
              onClick={(e) => e.stopPropagation()}
            />
          ) : (
            <h5 style={{ margin: '0 0 4px 0', fontSize: '14px', fontWeight: 'bold' }}>
              {card.title}
              {card.isAdminOnly && (
                <span style={{ 
                  marginLeft: '8px', 
                  fontSize: '12px',
                  backgroundColor: 'var(--color-red)',
                  color: 'white',
                  padding: '2px 6px',
                  borderRadius: '3px'
                }}>
                  Admin
                </span>
              )}
            </h5>
          )}

          {!isEditing && (
            <small style={{ 
              opacity: 0.8, 
              fontSize: '11px',
              display: 'block' 
            }}>
              {card.description}
            </small>
          )}
        </div>

        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-end', gap: '2px', marginLeft: '10px' }}>
          <div style={{ 
            backgroundColor: isActive ? 'rgba(255,255,255,0.2)' : '#6c757d',
            color: 'white',
            borderRadius: '12px',
            padding: '2px 8px',
            fontSize: '11px',
            fontWeight: 'bold'
          }}>
            Krok {index + 1}
          </div>

          <div style={{ 
            backgroundColor: isActive ? 'rgba(255,255,255,0.1)' : 'var(--color-green)',
            color: 'white',
            borderRadius: '8px',
            padding: '1px 6px',
            fontSize: '10px',
            fontWeight: 'bold'
          }}>
            {card.steps.length} {card.steps.length === 1 ? 'blok' : card.steps.length < 5 ? 'bloky' : 'blok콢'}
          </div>
        </div>
      </div>

      {/* Card Actions */}
      <div style={{
        position: 'absolute',
        top: '4px',
        right: '4px',
        display: 'flex',
        gap: '4px',
        opacity: isActive ? 1 : 0.7
      }}>
        {!canEdit && (
          <span style={{ fontSize: '16px' }}>游</span>
        )}

        {canDelete && (
          <button
            onClick={(e) => {
              e.stopPropagation();
              onDelete();
            }}
            style={{
              background: 'none',
              border: 'none',
              fontSize: '14px',
              cursor: 'pointer',
              padding: '2px',
              borderRadius: '2px',
              color: isActive ? 'rgba(255,255,255,0.8)' : 'var(--color-red)'
            }}
            title="Smazat tento krok"
