const TokenList = () => {
  const { user } = useAuth();
  const [tokens, setTokens] = React.useState([]);
  const [surveys, setSurveys] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState(null);
  const [showCreateForm, setShowCreateForm] = React.useState(false);
  const [newToken, setNewToken] = React.useState({
    surveyId: '',
    allowedDomains: '',
    validFrom: '',
    validTo: ''
  });

  // Fetch tokens and surveys
  React.useEffect(() => {
    fetchTokens();
    fetchSurveys();
  }, []);

  const fetchTokens = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch('/api/tokens', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setTokens(data.tokens || []);
      } else {
        throw new Error('Chyba p콏i na캜칤t치n칤 token콢');
      }
    } catch (error) {
      console.error('Error fetching tokens:', error);
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchSurveys = async () => {
    try {
      const response = await fetch('/api/surveys', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setSurveys(data.surveys || []);
      }
    } catch (error) {
      console.error('Error fetching surveys:', error);
    }
  };

  const handleCreateToken = async (e) => {
    e.preventDefault();

    if (!newToken.surveyId) {
      alert('Vyberte pros칤m dotazn칤k');
      return;
    }

    try {
      const response = await fetch('/api/tokens', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          survey_id: newToken.surveyId,
          allowed_domains: newToken.allowedDomains.split('\n').filter(d => d.trim()),
          valid_from: newToken.validFrom || null,
          valid_until: newToken.validTo || null
        })
      });

      if (response.ok) {
        await fetchTokens();
        setShowCreateForm(false);
        setNewToken({ surveyId: '', allowedDomains: '', validFrom: '', validTo: '' });
        alert('Token byl 칰sp캩코n캩 vytvo콏en!');
      } else {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Chyba p콏i vytv치콏en칤 tokenu');
      }
    } catch (error) {
      console.error('Error creating token:', error);
      alert('Chyba p콏i vytv치콏en칤 tokenu: ' + error.message);
    }
  };

  const handleToggleStatus = async (tokenId, currentStatus) => {
    const newStatus = currentStatus === 'active' ? 'paused' : 'active';
    const action = newStatus === 'active' ? 'obnovit' : 'pozastavit';
    const endpoint = newStatus === 'active' ? 'resume' : 'pause';

    if (window.confirm(`Opravdu chcete ${action} tento token?`)) {
      try {
        const response = await fetch(`/api/tokens/${tokenId}/${endpoint}`, {
          method: 'PATCH',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });

        if (response.ok) {
          await fetchTokens();
          alert(`Token byl ${newStatus === 'active' ? 'obnoven' : 'pozastaven'}`);
        } else {
          throw new Error('Chyba p콏i zm캩n캩 statusu');
        }
      } catch (error) {
        console.error('Error updating token:', error);
        alert('Chyba p콏i zm캩n캩 statusu: ' + error.message);
      }
    }
  };

  const handleDeleteToken = async (tokenId, tokenName) => {
    if (window.confirm(`Opravdu chcete smazat token "${tokenName}"?`)) {
      try {
        const response = await fetch(`/api/tokens/${tokenId}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });

        if (response.ok) {
          await fetchTokens();
          alert('Token byl smaz치n');
        } else {
          throw new Error('Chyba p콏i maz치n칤 tokenu');
        }
      } catch (error) {
        console.error('Error deleting token:', error);
        alert('Chyba p콏i maz치n칤 tokenu: ' + error.message);
      }
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text).then(() => {
      alert('K칩d byl zkop칤rov치n do schr치nky!');
    }).catch(() => {
      alert('Chyba p콏i kop칤rov치n칤 do schr치nky');
