const UserList = () => {
  const [users, setUsers] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [editingUser, setEditingUser] = React.useState(null);
  const [showCreateForm, setShowCreateForm] = React.useState(false);

  // Fetch users
  React.useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/users', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      if (response.ok) {
        const data = await response.json();
        // Handle API response format - it returns {users: [...], pagination: {...}}
        setUsers(data.users || data);
      }
    } catch (error) {
      console.error('Error fetching users:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (user) => {
    setEditingUser({...user, newPassword: ''});
  };

  const handleSave = async () => {
    try {
      const { id, newPassword, ...updateData } = editingUser;
      if (newPassword) {
        updateData.password = newPassword;
      }

      const response = await fetch(`/api/users/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(updateData)
      });

      if (response.ok) {
        await fetchUsers();
        setEditingUser(null);
        alert('U쬴vatel byl 칰sp캩코n캩 upraven');
      } else {
        alert('Chyba p콏i ukl치d치n칤 u쬴vatele');
      }
    } catch (error) {
      console.error('Error saving user:', error);
      alert('Chyba p콏i ukl치d치n칤 u쬴vatele');
    }
  };

  const handleDelete = async (userId, username) => {
    if (window.confirm(`Opravdu chcete smazat u쬴vatele "${username}"?`)) {
      try {
        const response = await fetch(`/api/users/${userId}`, {
          method: 'DELETE',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });

        if (response.ok) {
          await fetchUsers();
          alert('U쬴vatel byl smaz치n');
        } else {
          alert('Chyba p콏i maz치n칤 u쬴vatele');
        }
      } catch (error) {
        console.error('Error deleting user:', error);
        alert('Chyba p콏i maz치n칤 u쬴vatele');
      }
    }
  };

  const handleCreate = async (userData) => {
    try {
      const response = await fetch('/api/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(userData)
      });

      if (response.ok) {
        await fetchUsers();
        setShowCreateForm(false);
        alert('U쬴vatel byl vytvo콏en');
      } else {
        const error = await response.json();
        alert('Chyba p콏i vytv치콏en칤 u쬴vatele: ' + error.error);
      }
    } catch (error) {
      console.error('Error creating user:', error);
      alert('Chyba p콏i vytv치콏en칤 u쬴vatele');
    }
  };

  if (loading) {
    return <div style={{ padding: '20px' }}>Na캜칤t치n칤 u쬴vatel콢...</div>;
  }

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
        <h2>Spr치va u쬴vatel콢</h2>
        <button 
          onClick={() => setShowCreateForm(true)}
          style={{ padding: '8px 16px', backgroundColor: 'var(--color-green)', color: 'white', border: 'none', borderRadius: '4px' }}
        >
          P콏idat u쬴vatele
        </button>
      </div>

      {showCreateForm && (
        <UserForm 
          onSave={handleCreate}
          onCancel={() => setShowCreateForm(false)}
          title="Vytvo콏it nov칠ho u쬴vatele"
        />
      )}

      {editingUser && (
        <UserForm 
          user={editingUser}
          onSave={() => handleSave()}
          onCancel={() => setEditingUser(null)}
          onChange={setEditingUser}
          title="Upravit u쬴vatele"
        />
      )}

      <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
        <thead>
          <tr style={{ backgroundColor: '#f8f9fa' }}>
            <th style={{ padding: '12px', textAlign: 'left', border: '1px solid #dee2e6' }}>U쬴vatelsk칠 jm칠no</th>
            <th style={{ padding: '12px', textAlign: 'left', border: '1px solid #dee2e6' }}>Email</th>
            <th style={{ padding: '12px', textAlign: 'left', border: '1px solid #dee2e6' }}>Role</th>
            <th style={{ padding: '12px', textAlign: 'left', border: '1px solid #dee2e6' }}>Vytvo콏eno</th>
            <th style={{ padding: '12px', textAlign: 'left', border: '1px solid #dee2e6' }}>Akce</th>
          </tr>
        </thead>
        <tbody>
          {users.map(user => (
            <tr key={user.id}>
              <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>{user.username}</td>
              <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>{user.email}</td>
              <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>
                <span style={{ 
                  padding: '4px 8px', 
                  borderRadius: '8px',
                  backgroundColor: user.role === 'admin' ? 'var(--color-tag-bg-red)' : 'var(--color-tag-bg-green)',
                  color: user.role === 'admin' ? '#B32222' : '#368537',
                  fontSize: '12px'
                }}>
                  {user.role}
                </span>
              </td>
              <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>
                {new Date(user.created_at).toLocaleDateString('cs-CZ')}
              </td>
              <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>
                <button 
                  onClick={() => handleEdit(user)}
                  style={{ padding: '4px 8px', marginRight: '8px', backgroundColor: '#ffc107', border: 'none', borderRadius: '3px' }}
                >
                  Upravit
                </button>
                <button 
                  onClick={() => handleDelete(user.id, user.username)}
                  style={{ padding: '4px 8px', backgroundColor: 'var(--color-red)', color: 'white', border: 'none', borderRadius: '3px' }}
                >
                  Smazat
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {users.length === 0 && (
        <div style={{ textAlign: 'center', padding: '40px', color: '#6c757d' }}>
          콯치dn칤 u쬴vatel칠 nebyli nalezeni
        </div>
      )}
    </div>
  );
};

const AuditLogs = () => {
  const [logs, setLogs] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [filters, setFilters] = React.useState({
    eventType: '',
    userId: '',
    dateFrom: '',
    dateTo: '',
    page: 1,
    limit: 50
  });

  // Fetch logs
  React.useEffect(() => {
    fetchLogs();
  }, [filters]);

  const fetchLogs = async () => {
    try {
      setLoading(true);
      const queryParams = new URLSearchParams();

      // Map frontend filters to backend API parameters
      if (filters.eventType) queryParams.append('action', filters.eventType);
      if (filters.userId) queryParams.append('user_id', filters.userId);
      if (filters.dateFrom) queryParams.append('from_date', filters.dateFrom + 'T00:00:00Z');
      if (filters.dateTo) queryParams.append('to_date', filters.dateTo + 'T23:59:59Z');
      if (filters.page) queryParams.append('page', filters.page);
      if (filters.limit) queryParams.append('limit', filters.limit);

      const response = await fetch(`/api/logs?${queryParams}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setLogs(data.logs || data);
      }
    } catch (error) {
      console.error('Error fetching logs:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (field, value) => {
    setFilters(prev => ({ ...prev, [field]: value, page: 1 }));
  };

  const getEventTypeColor = (eventType) => {
    const colors = {
      'LOGIN': 'var(--color-green)',
      'LOGOUT': 'var(--color-gray)',
      'CREATE': 'var(--color-blue)',
      'UPDATE': 'var(--color-yellow)',
      'DELETE': 'var(--color-red)',
      'TOKEN_CREATE': '#17a2b8',
      'TOKEN_PAUSE': '#fd7e14',
      'TOKEN_RESUME': '#20c997',
      'TOKEN_DELETE': '#e83e8c',
      'SURVEY_DELIVERY': '#6f42c1'
    };
    return colors[eventType] || 'var(--color-gray)';
  };

  const formatEventDescription = (log) => {
    switch(log.action) {
      case 'LOGIN':
        return `U쬴vatel se p콏ihl치sil`;
      case 'LOGOUT':
        return `U쬴vatel se odhl치sil`;
      case 'CREATE':
        return `Vytvo콏il ${log.resource_type}: ${log.resource_id}`;
      case 'UPDATE':
        return `Upravil ${log.resource_type}: ${log.resource_id}`;
      case 'DELETE':
        return `Smazal ${log.resource_type}: ${log.resource_id}`;
      case 'TOKEN_CREATE':
        return `Vytvo콏il token pro survey: ${log.resource_id}`;
      case 'TOKEN_PAUSE':
        return `Pozastavil token: ${log.resource_id}`;
      case 'TOKEN_RESUME':
        return `Obnovil token: ${log.resource_id}`;
      case 'TOKEN_DELETE':
        return `Smazal token: ${log.resource_id}`;
      case 'SURVEY_DELIVERY':
        return `Survey doru캜en p콏es token: ${log.resource_id}`;
      default:
        return log.action;
    }
  };

  if (loading) {
    return <div style={{ padding: '20px' }}>Na캜칤t치n칤 audit log콢...</div>;
  }

  return (
    <div style={{ padding: '20px' }}>
      <h2>Z치znamy</h2>
      <p style={{ color: '#6c757d', marginBottom: '20px' }}>
        Kompletn칤 z치znam v코ech aktivit v syst칠mu
      </p>

      {/* Filters */}
      <div style={{ 
        backgroundColor: '#f8f9fa', 
        padding: '20px', 
        borderRadius: '8px', 
        marginBottom: '20px',
        border: '1px solid #dee2e6'
      }}>
        <h4 style={{ marginBottom: '15px' }}>Filtry</h4>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: '15px' }}>
          <div>
            <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
              Typ ud치losti
            </label>
            <select
              value={filters.eventType}
              onChange={(e) => handleFilterChange('eventType', e.target.value)}
              style={{ width: '100%', padding: '8px', border: '1px solid #ced4da', borderRadius: '8px' }}
            >
              <option value="">V코echny</option>
              <option value="LOGIN">P콏ihl치코en칤</option>
              <option value="LOGOUT">Odhl치코en칤</option>
              <option value="CREATE">Vytvo콏en칤</option>
              <option value="UPDATE">칔prava</option>
              <option value="DELETE">Smaz치n칤</option>
              <option value="TOKEN_CREATE">Vytvo콏en칤 tokenu</option>
              <option value="TOKEN_PAUSE">Pozastaven칤 tokenu</option>
              <option value="TOKEN_RESUME">Obnoven칤 tokenu</option>
              <option value="TOKEN_DELETE">Smaz치n칤 tokenu</option>
              <option value="SURVEY_DELIVERY">Doru캜en칤 survey</option>
            </select>
          </div>

          <div>
            <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
              Od data
            </label>
            <input
              type="date"
              value={filters.dateFrom}
              onChange={(e) => handleFilterChange('dateFrom', e.target.value)}
              placeholder="dd/mm/rrrr"
              style={{ width: '100%', padding: '8px', border: '1px solid #ced4da', borderRadius: '8px' }}
