            <tr style={{ backgroundColor: '#f8f9fa'}}>
              <th style={{ padding: '12px', textAlign: 'left', border: '1px solid #dee2e6', minWidth: '150px'}}>Datum a 캜as</th>
              <th style={{ padding: '12px', textAlign: 'left', border: '1px solid #dee2e6', minWidth: '80px' }}>U쬴vatel</th>
