          <tbody>
            {logs.map(log => (
              <tr key={log.id} style={{ borderBottom: '1px solid #dee2e6' }}>
                <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>
                  {new Date(log.timestamp).toLocaleString('cs-CZ')}
                </td>
                <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>
                  {log.username || log.user_id || 'N/A'}
                </td>
                <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>
                  <span style={{ 
                    padding: '4px 8px', 
                    borderRadius: '4px',
                    backgroundColor: getEventTypeColor(log.action),
                    color: 'white',
                    fontSize: '12px',
                    fontWeight: 'bold'
                  }}>
                    {log.action}
                  </span>
                </td>
                <td style={{ padding: '12px', border: '1px solid #dee2e6' }}>
                  {formatEventDescription(log)}
                  {log.details && (
                    <div style={{ fontSize: '12px', color: '#6c757d', marginTop: '4px' }}>
                      {typeof log.details === 'string' ? log.details : JSON.stringify(log.details)}
                    </div>
                  )}
                </td>
                <td style={{ padding: '12px', border: '1px solid #dee2e6', fontFamily: 'monospace' }}>
                  {log.ip_address || 'N/A'}
                </td>
                <td style={{ padding: '12px', border: '1px solid #dee2e6', fontSize: '12px', color: '#6c757d' }}>
                  {log.user_agent ? log.user_agent.substring(0, 50) + (log.user_agent.length > 50 ? '...' : '') : 'N/A'}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {logs.length === 0 && (
        <div style={{ textAlign: 'center', padding: '40px', color: '#6c757d' }}>
          콯치dn칠 audit logy nebyly nalezeny
        </div>
      )}

      {/* Pagination would go here if needed */}
      <div style={{ marginTop: '20px', textAlign: 'center', color: '#6c757d' }}>
        Zobrazeno {logs.length} z치znam콢
      </div>
    </div>
  );
};

const UserForm = ({ user, onSave, onCancel, onChange, title }) => {
  const [formData, setFormData] = React.useState({
    username: user?.username || '',
    email: user?.email || '',
    password: '',
    confirmPassword: '',
    role: user?.role || 'user'
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.username || !formData.email) {
      alert('U쬴vatelsk칠 jm칠no a email jsou povinn칠');
      return;
    }
    if (!user && !formData.password) {
      alert('Heslo je povinn칠 pro nov칠ho u쬴vatele');
      return;
    }
    if (formData.password && formData.password !== formData.confirmPassword) {
      alert('Hesla se neshoduj칤');
      return;
    }

    const dataToSave = { ...formData };
    if (user && !formData.password) {
      delete dataToSave.password; // Don't update password if not provided
    }
    delete dataToSave.confirmPassword; // Remove confirmPassword from data to save

    if (onChange) {
      onChange({ ...user, ...dataToSave, newPassword: formData.password });
    } else {
      onSave(dataToSave);
    }
  };

  const handleChange = (field, value) => {
    const newData = { ...formData, [field]: value };
    setFormData(newData);
    if (onChange) {
      onChange({ ...user, ...newData, newPassword: newData.password });
    }
  };

  return (
    <div style={{ 
      backgroundColor: '#f8f9fa', 
      padding: '20px', 
      borderRadius: '8px', 
      marginBottom: '20px',
      border: '1px solid #dee2e6'
    }}>
      <h3>{title}</h3>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            U쬴vatelsk칠 jm칠no *
          </label>
          <input
            type="text"
            value={formData.username}
            onChange={(e) => handleChange('username', e.target.value)}
            style={{ width: '100%', padding: '8px', border: '1px solid #ced4da', borderRadius: '4px' }}
            required
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            Email *
          </label>
          <input
            type="email"
            value={formData.email}
            onChange={(e) => handleChange('email', e.target.value)}
            style={{ width: '100%', padding: '8px', border: '1px solid #ced4da', borderRadius: '4px' }}
            required
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            {user ? 'Nov칠 heslo (ponechte pr치zdn칠 pro nezm캩nu)' : 'Heslo *'}
          </label>
          <input
            type="password"
            value={formData.password}
            onChange={(e) => handleChange('password', e.target.value)}
            style={{ width: '100%', padding: '8px', border: '1px solid #ced4da', borderRadius: '4px' }}
            required={!user}
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            {user ? 'Potvrzen칤 nov칠ho hesla' : 'Potvrzen칤 hesla *'}
          </label>
          <input
            type="password"
            value={formData.confirmPassword}
            onChange={(e) => handleChange('confirmPassword', e.target.value)}
            style={{ width: '100%', padding: '8px', border: '1px solid #ced4da', borderRadius: '4px' }}
            required={!user}
          />
        </div>

        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
            Role
          </label>
          <select
            value={formData.role}
            onChange={(e) => handleChange('role', e.target.value)}
            style={{ width: '100%', padding: '8px', border: '1px solid #ced4da', borderRadius: '4px' }}
          >
            <option value="user">User</option>
            <option value="admin">Admin</option>
          </select>
        </div>

        <div style={{ display: 'flex', gap: '10px' }}>
          <button 
            type="submit"
            style={{ padding: '8px 16px', backgroundColor: 'var(--color-green)', color: 'white', border: 'none', borderRadius: '4px' }}
          >
            {user ? 'Ulo쬴t zm캩ny' : 'Vytvo콏it u쬴vatele'}
          </button>
          <button 
            type="button"
            onClick={onCancel}
            style={{ padding: '8px 16px', backgroundColor: '#6c757d', color: 'white', border: 'none', borderRadius: '4px' }}
          >
            Zru코it
          </button>
        </div>
      </form>
    </div>
  );
};

const Settings = () => {
  const { user, updateProfile } = useAuth();
  const [formData, setFormData] = React.useState({
    username: user?.username || '',
    email: user?.email || '',
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (formData.newPassword && formData.newPassword !== formData.confirmPassword) {
      alert('Nov칠 heslo a potvrzen칤 hesla se neshoduj칤');
      return;
    }

    const updateData = {
      username: formData.username,
      email: formData.email
    };

    if (formData.newPassword) {
      updateData.currentPassword = formData.currentPassword;
      updateData.newPassword = formData.newPassword;
    }

    const result = await updateProfile(updateData);
    if (result.success) {
      alert('Profil byl 칰sp캩코n캩 aktualizov치n');
      setFormData(prev => ({ ...prev, currentPassword: '', newPassword: '', confirmPassword: '' }));
    } else {
      alert('Chyba p콏i aktualizaci profilu: ' + result.error);
